
$(document).ready(function(){
$("#less-link1").click(function(){
  if ( $('#less-text1').css("display", "none")){
  $('#less-text1').toggle()
} else {
   $('#less-text1').css("display", "block")
   $('#less-text1').toggle()
}


})



$("#less-link2").click(function(){
  if ( $('#less-text2').css("display", "none")){
  $('#less-text2').toggle()
} else {
   $('#less-text2').css("display", "block")
   $('#less-text2').toggle()
}



})
})
